      out.write("								$(\"#editEnd\").val(clickedEvent.endStr);\n");
      out.write("								$(\"#editColor\").val(\n");
      out.write("										clickedEvent.backgroundColor);\n");
      out.write("								// 수정 모달 열기\n");
      out.write("								$(\"#editModal\").modal(\"show\");\n");
      out.write("							},\n");
      out.write("							select : function(arg) { // 캘린더에서 드래그로 이벤트를 생성할 수 있다.\n");
      out.write("								var title = prompt('Event Title:');\n");
      out.write("								if (title) {\n");
