      out.write("							res.forEach(function(event) {\n");
      out.write("								calendar.addEvent(event);\n");
      out.write("							});\n");
      out.write("						},\n");
      out.write("						error : function() {\n");
      out.write("							console.log(\"이벤트 불러오기 실패\");\n");
      out.write("						}\n");
      out.write("					});\n");
      out.write("				}\n");
      out.write("\n");
      out.write("				// 모달창 이벤트 // 일정 추가해서 화면에 띄워주기!\n");
      out.write("				$(\"#saveChanges\").on(\n");
      out.write("						\"click\",\n");
      out.write("						function() {\n");
      out.write("							var eventData = {\n");
      out.write("								title : $(\"#title\").val(),\n");
