      out.write("								color : $(\"#color\").val(),\n");
      out.write("							};\n");
      out.write("							//빈값입력시 오류\n");
      out.write("							if (eventData.title == \"\" || eventData.start == \"\"\n");
      out.write("									|| eventData.end == \"\") {\n");
      out.write("								alert(\"입력하지 않은 값이 있습니다.\");\n");
      out.write("\n");
      out.write("								//끝나는 날짜가 시작하는 날짜보다 값이 크면 안됨\n");
      out.write("							} else if ($(\"#start\").val() > $(\"#end\").val()) {\n");
      out.write("								alert(\"시간을 잘못입력 하셨습니다.\");\n");
      out.write("							} else {\n");
      out.write("								// 이벤트 추가\n");
      out.write("								$(\"#exampleModal\").modal(\"hide\");\n");
      out.write("								$(\"#title\").val(\"\");\n");
      out.write("								$(\"#start\").val(\"\");\n");
      out.write("								$(\"#end\").val(\"\");\n");
      out.write("								$(\"#color\").val(\"\");\n");
      out.write("								// db에 저장\n");
      out.write("								$.ajax({\n");
      out.write("									url : 'CalendarSave.do',\n");
      out.write("									method : 'POST',\n");
      out.write("									data : eventData,\n");
      out.write("									success : function(response) {\n");
      out.write("										console.log(\"성공\");\n");
      out.write("										calendar.addEvent(eventData);\n");
