      out.write("								cal_idx : clickedEvent.id, // clickedEvent 변수 사용\n");
      out.write("							};\n");
      out.write("\n");
      out.write("							$.ajax({\n");
      out.write("								url : \"CalendarUpdate.do\",\n");
      out.write("								method : 'POST',\n");
      out.write("								data : eventData,\n");
      out.write("								success : function(response) {\n");
      out.write("									clickedEvent.setProp('title',\n");
